css:
.PortalStatusBarIndicator.PortalStatusBarIndicator.PortalStatusBarIndicator:before {
  margin-right: 0;
}
.PortalStatusBarIndicator.transmitting {
  color: #5293d8;
}
.PortalStatusBarIndicator.transmitting:hover {
  color: #5293d8;
}
.PortalStatusBarIndicator.outdated,
.PortalStatusBarIndicator.initialization-error {
  color: #c00;
}
.PortalStatusBarIndicator.outdated:hover,
.PortalStatusBarIndicator.initialization-error:hover {
  color: #c00;
}
.TeletypePopoverTooltip {
  user-select: none;
}
.TeletypePopoverTooltip.tooltip {
  width: 315px;
  padding-left: 10px;
  padding-right: 10px;
  box-sizing: border-box;
}
.TeletypePopoverTooltip .tooltip-inner.tooltip-inner.tooltip-inner.tooltip-inner {
  padding: 10px;
  background-color: #f4f4f4;
  border: 1px solid #9f9f9f;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  color: #444;
  max-height: 300px;
  white-space: normal;
  overflow-y: auto;
}
.TeletypePopoverTooltip.top .tooltip-arrow.tooltip-arrow {
  width: 10px;
  height: 10px;
  border-width: 0 0 1px 1px;
  border-color: #9f9f9f;
  background-color: #f4f4f4;
  border-bottom-right-radius: 2px;
  transform: rotate(-45deg);
}
.TeletypePopoverComponent {
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: -10px;
  color: #444 !important;
}
.PortalListComponent {
  flex: 1;
  display: flex;
  flex-direction: column;
  height: 100%;
}
.PortalListComponent--initializing {
  text-align: center;
}
.PortalParticipants {
  flex: auto;
  display: flex;
  flex-direction: row;
  align-items: center;
}
.PortalParticipants-guests {
  flex: auto;
  display: flex;
  flex-direction: row;
  align-items: center;
  align-content: start;
  flex-wrap: wrap;
}
.PortalParticipants-participant {
  margin: 5px;
}
.PortalParticipants-participant img {
  height: 22px;
  border-radius: 50%;
}
.PortalParticipants-site-1 {
  margin-left: 0;
}
.PortalParticipants-site-1 img {
  height: 28px;
}
.HostPortalComponent {
  flex: none;
}
.HostPortalComponent-status {
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 0 10px;
}
.HostPortalComponent-share-toggle {
  flex: none;
}
.HostPortalComponent-share-toggle label {
  margin: 0;
  font-size: 80%;
}
.HostPortalComponent-connection-info,
.HostPortalComponent-GuestPortalsContainer {
  border-bottom: 1px solid #9f9f9f;
  padding: 0 10px;
}
.HostPortalComponent-connection-info {
  display: flex;
  flex-direction: column;
  align-items: stretch;
  padding: 10px 10px 5px;
  position: relative;
}
.HostPortalComponent-connection-info-heading.creating-portal {
  visibility: hidden;
}
.HostPortalComponent-connection-info-heading h1 {
  margin-top: 0;
  margin-bottom: 5px;
  font-weight: normal;
  font-size: 80%;
}
.HostPortalComponent-connection-info-heading h1::before {
  line-height: 1em;
  margin-right: 5px;
}
.HostPortalComponent-connection-info-portal-url {
  display: flex;
  flex-direction: row;
  margin-bottom: 5px;
}
.HostPortalComponent-connection-info-portal-url.creating-portal {
  visibility: hidden;
}
.HostPortalComponent-connection-info-portal-url .input-text {
  display: flex;
  flex: auto;
}
.HostPortalComponent-connection-info-portal-url .btn {
  margin-left: 5px;
  flex: none;
}
.HostPortalComponent-connection-info-spinner {
  position: absolute !important;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
}
.HostPortalComponent .host-id-input {
  font-family: Menlo, Consolas, 'DejaVu Sans Mono', monospace;
  font-size: 80%;
  color: #444;
}
.PortalListComponent-GuestPortalsContainer {
  flex: 1;
}
.GuestPortalComponent {
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 0 10px;
}
.GuestPortalComponent-leave.btn {
  flex: none;
  width: 50px;
}
.GuestPortalComponent-leave.btn label {
  margin: 0;
  font-size: 80%;
}
.SignInComponent {
  text-align: center;
  padding: 10px;
}
.SignInComponent atom-text-editor {
  font-size: 12px !important;
  margin-bottom: 10px;
  text-align: left;
}
.SignInComponent-GitHubLogo::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 40px;
  width: 40px;
  height: 40px;
  content: "\f00a";
}
.JoinViaExternalAppDialog {
  padding: 10px;
}
.JoinViaExternalAppDialog-cancel {
  position: absolute;
  padding: 10px;
  top: 0;
  right: 0;
  text-align: right;
}
.JoinViaExternalAppDialog-cancel::before {
  margin-right: 0;
}
.JoinViaExternalAppDialog-uri {
  padding: 5px 10px;
  color: #000;
  border: 1px solid #9f9f9f;
  border-radius: 2px;
  background-color: rgba(0, 0, 0, 0.1);
}
.JoinViaExternalAppDialog-footer {
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.JoinViaExternalAppDialog .input-label {
  margin-left: 2em;
  margin-right: 3em;
}
.JoinViaExternalAppDialog .input-checkbox {
  margin-left: -2em;
  margin-right: .5em;
}
.JoinPortalComponent--no-prompt,
.JoinPortalComponent--prompt {
  border-top: 1px solid #9f9f9f;
}
.JoinPortalComponent--no-prompt {
  flex: none;
  height: 40px;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
}
.JoinPortalComponent--no-prompt label {
  color: #999;
  font-size: 12px;
  margin: 0;
}
.JoinPortalComponent--no-prompt label:hover {
  color: #fff;
  cursor: pointer;
}
.JoinPortalComponent--prompt {
  flex: none;
  height: 40px;
  text-align: left;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  padding: 0 10px;
}
.JoinPortalComponent--prompt atom-text-editor {
  flex: auto;
  font-size: 80% !important;
}
.JoinPortalComponent--prompt .btn {
  flex: none;
  width: 50px;
  margin-left: 5px;
}
.cursors.blink-off .cursor.non-blinking {
  opacity: 1;
}
.SitePositionsComponent {
  display: flex;
  flex-direction: row;
  position: absolute;
  justify-content: flex-end;
  font-size: 16px;
  text-align: right;
  left: 0;
  right: 15px;
  bottom: 15px;
  overflow: hidden;
  pointer-events: none;
}
.SitePositionsComponent img {
  cursor: pointer;
  height: 40px;
  border: 2px solid transparent;
  border-radius: 50%;
}
.SitePositionsComponent .SitePositionsComponent-site {
  margin-left: 5px;
  pointer-events: auto;
  border: solid 2px transparent;
  border-radius: 50%;
}
.SitePositionsComponent .SitePositionsComponent-site.viewing-non-portal-item {
  opacity: 0.5;
}
.SitePositionsComponent .SitePositionsComponent-site .icon-link {
  position: absolute;
  margin-left: 26px;
  margin-top: 26px;
  color: #5293d8;
  pointer-events: none;
}
.SitePositionsComponent .SitePositionsComponent-site .icon-link::before {
  position: relative;
  z-index: 2;
}
.SitePositionsComponent .SitePositionsComponent-site .icon-link::after {
  content: "";
  position: absolute;
  z-index: 1;
  left: -2px;
  top: 4px;
  width: 15px;
  height: 11px;
  border-radius: 6px;
  background-color: #fff;
}
.cursor.ParticipantCursor--site-1,
.SitePositionsComponent-site.color--site-1 {
  border-color: #17ca65;
}
.cursor.ParticipantCursor--site-2,
.SitePositionsComponent-site.color--site-2 {
  border-color: #0098ff;
}
.cursor.ParticipantCursor--site-3,
.SitePositionsComponent-site.color--site-3 {
  border-color: #ff4800;
}
.cursor.ParticipantCursor--site-4,
.SitePositionsComponent-site.color--site-4 {
  border-color: #db2ff4;
}
.cursor.ParticipantCursor--site-5,
.SitePositionsComponent-site.color--site-5 {
  border-color: #f5e11d;
}
.cursor.ParticipantCursor--site-6,
.SitePositionsComponent-site.color--site-6 {
  border-color: #17cab0;
}
.cursor.ParticipantCursor--site-7,
.SitePositionsComponent-site.color--site-7 {
  border-color: #002eff;
}
.cursor.ParticipantCursor--site-8,
.SitePositionsComponent-site.color--site-8 {
  border-color: #ffb200;
}
.cursor.ParticipantCursor--site-9,
.SitePositionsComponent-site.color--site-9 {
  border-color: #f42fbb;
}
.cursor.ParticipantCursor--site-10,
.SitePositionsComponent-site.color--site-10 {
  border-color: #aff51d;
}
.cursor.ParticipantCursor--site-11,
.SitePositionsComponent-site.color--site-11 {
  border-color: #176dca;
}
.cursor.ParticipantCursor--site-12,
.SitePositionsComponent-site.color--site-12 {
  border-color: #7c00ff;
}
.cursor.ParticipantCursor--site-13,
.SitePositionsComponent-site.color--site-13 {
  border-color: #a2ff00;
}
.cursor.ParticipantCursor--site-14,
.SitePositionsComponent-site.color--site-14 {
  border-color: #f42f38;
}
.cursor.ParticipantCursor--site-15,
.SitePositionsComponent-site.color--site-15 {
  border-color: #1ff51d;
}
.PackageOutdatedComponent,
.PackageInitializationErrorComponent {
  color: #444;
  text-align: center;
  padding: 0 10px 10px 10px;
}
.PackageOutdatedComponent .btn,
.PackageInitializationErrorComponent .btn {
  margin-bottom: 15px;
}
